<?php

namespace WebsiteApi\PaymentsBundle\Repository;

/**
 * GroupIdentityRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GroupIdentityRepository extends \WebsiteApi\CoreBundle\Services\DoctrineAdapter\RepositoryAdapter
{
    public function findByLockDateExpire()
    {
        $qb = $this->createQueryBuilder('u');
        $qb->where('u.lockDate IS NOT NULL')
            ->andWhere('u.lockDate >= :current_date')
            ->setParameter('current_date', new \DateTime());

        return $qb->getQuery()
            ->getResult();
    }
}
